import React from 'react';
// import Recaptcha from 'react-recaptcha';
import './RegisterForm.css';
import axios from 'axios';
import { loadReCaptcha } from 'react-recaptcha-google';
import { ReCaptcha } from 'react-recaptcha-google';


class Registerform extends React.Component {



    constructor(props) {
        super(props);

        this.state = {
            email: '',
            fields: {},
            errors:{}
        };
        

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);

    }
    // componentDidMount() {
    //     const script = document.getElementsByClassName("g-recaptcha")

    //     script.src = "https://www.google.com/recaptcha/api.js";
    //     script.async = true;

    //     document.body.appendChild(script);
    // }

    validateForm(){
        let fields = this.state.fields;
        let errors = {};
        let formisValid = true;

        if(!fields["email"]){
            formisValid = false;
            errors["email"] = "Please Enter your mail-id";
        }
        if (typeof fields["email"] !== "undefined") {
            //regular expression for email validation
            var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
            if (!pattern.test(fields["email"])) {
                formIsValid = false;
                errors["email"] = "*Please enter valid email-ID.";
            }
        }
        this.setState({
            errors: errors
        });
        return formisValid;
    }

    handleSubmit = async e => {
        e.preventDefault();
        console.log(this.state);
        
        var data = { email: this.state.email };

        
        fetch('http://localhost:5000/register', {
            method: 'POST',
            body: JSON.stringify(data),
            headers: { 'Content-Type': 'application/json' }
        })
        .then(response => response.json())
        .then((info) => { console.log(info) })
    }
    

    
    // handle the state changed 
    handleChange(event) {
        let fields = this.state.fields;
        this.setState({
            [event.target.id]: event.target.value,
            fields
            // grab the state property
        })

    }
    componentDidMount() {
        loadReCaptcha();
        if (this.captchaDemo) {
            console.log("started, just a second...")
            this.captchaDemo.reset();
    };
}
    
    verifyCaptcha = (res) => {
        if (res) {
            this.setState({ human: true, humanKey: res })
            this.setState({ disabled: this.isDisabled() })
        }
    }
    expireCaptcha = () => {
        this.setState({ human: false, humanKey: null });
    }



    render() {
        return (
            <div className="form-div">
                <form method="post" action="/register" onSubmit={this.handleSubmit}>
                    Email : <br />
                    <input type="text" id="email" onChange={this.handleChange} />

                    <div className="Captcha">
                        <ReCaptcha
                            size="normal"
                            data-theme="dark"
                            render="explicit"
                            sitekey="6Le2qb0ZAAAAAFpP9p0d2QQOf7FBtuZXUryjQ1pP"
                            onChange={this.verifyCaptcha}
                            onExpired={this.expireCaptcha}
                        />
                    </div>
                    <br />
                    <input type="submit" id="btn" value="Register" />
                </form>
            </div>
        )
    }
}



export default Registerform;
